import React, { Component } from "react";
import GoogleMapReact from "google-map-react";

import marker from '../../../Assets/Images/pin.png'

const googleAPIKey = "AIzaSyCq1D3cnZycxn5PCwiahpHscgep3yoN35U";

const markerStyle = {
  position: "absolute",
  top: "100%",
  left: "100%",
  cursor: "pointer",
  transform: "translate(-50%, -100%)"
};


const Marker = ({ center, zoom, data, showPopup }) => {

      handleMarkerClick = (props) =>{
        this.setState({  
            showPopup: !props.showPopup  
       });  
    }

  return (
    <GoogleMapReact
      bootstrapURLKeys={{ key: googleAPIKey }}
      yesIWantToUseGoogleMapApiInternals={true}
      defaultZoom={zoom}
      defaultCenter={center}
    >
      {data.map(item => {

        return (
          <div 
            lat={item.latitude} 
            lng={item.longitude} 
            onClick={() => this.props.handleMarkerClick(props)}>
            <img style={markerStyle} src={marker} alt="pin" />
          </div>
        );
      })}
    </GoogleMapReact>
  );
};

 export default Marker;
