import { CardContent } from '@material-ui/core';
import Chart from './Chart.js'


export class LineChart extends Component {

    constructor(props) {
        super(props)
        this.state = {
          dropdownOpen: false,
          chartType : 'Pie',
          line: 'false',
          bar: 'false',
          area:'true'
        }
    }
    async handleLine(){
        await this.setState({ chartType:"Line", line:true, bar:false, area:false});
        this.refs.child.graphType();
    }
    async handleBar(){
        await this.setState({ chartType:"Bar", line:false, bar:true, area:false});
        this.refs.child.graphType();
    }
    async handlePie(){
        await this.setState({chartType:"Pie", line:false, bar:false, area:true});
        this.refs.child.graphType();
    }

    render() {
        const {line, bar, area} = this.state;
        console.log(line)
        console.log(bar)
        console.log(area)
        // console.log("ChartType in Line chart: ",this.state.chartType)
        return (

            <Card raised={true} className='card-cont'>

            <CardHeader
                action={
                    <div>
                        <IconButton color="outline-secondary" onClick = {()=> {this.handleLine()}}><img src={line ? lineIconSelected : lineIcon}  /></IconButton>
                        <IconButton color="outline-secondary" onClick = {()=> {this.handleBar()}}><img src={bar ? barIconSelected : barIcon}  /></IconButton>
                        <IconButton color="outline-secondary" onClick = {()=> {this.handlePie()}}><img src={area ? areaIconSelected : areaIcon}  /></IconButton>
