                        <IconButton onClick = {()=> {this.handleMap()}}><img src={mapType ? mapviewIconActive:mapviewIcon} style={{width:'15px'}} /></IconButton> */}
                    </div>
                }
                title="Parking Utilisation"
                titleTypographyProps={{variant:'h6' }}
                />
            <CardContent>
                <Chart ref="child" chartType={this.state.chartType} />
                <div className="card-footer">
                    <Button variant="contained" color="primary">Track the Parking status</Button>
                </div>
            </CardContent>
        </Card>




            // <Row>
