import React, { Component } from 'react'
import PropTypes from 'prop-types';

import { fade, makeStyles } from '@material-ui/core/styles';
import SearchIcon from '@material-ui/icons/Search';
import InputBase from '@material-ui/core/InputBase';

const search = {
    position: 'relative',
    borderRadius: '20px',
    backgroundColor: 'green',
    '&:hover': {
      backgroundColor: 'blue',
    },
    marginRight: '20px',
    marginLeft: 0,
    width: '100%',    
}

const inputField = {
    padding: '1.5px 1.5px 1.5px 7px',
    width: '100%',
}

const searchIcon = {
    // width: theme.spacing(7),
    height: '100%',
    position: 'absolute',
    right: '0',
    pointerEvents: 'none',
    display: 'inline-flex',
    alignItems: 'center',
    justifyContent: 'center',   
}

export class HeaderSearch extends Component {

    static propTypes = {
        options: PropTypes.instanceOf(Array).isRequired
      };
      state = {
        activeOption: 0,
        filteredOptions: [],
        showOptions: false,
        userInput: ''
      };

    onChange = (e) => {
        const { options } = this.props;
        const userInput = e.currentTarget.value;
        const filteredOptions = options.filter(
          (option) => option.toLowerCase().indexOf(userInput.toLowerCase()) > -1
        );
        this.setState({
            activeOption: 0,
            filteredOptions,
            showOptions: true,
            userInput
            });
    };

    onClick = (e) => {
        this.setState({
          activeOption: 0,
          filteredOption: [],
          showOptions: false,
          userInput: e.currentTarget.innerText
        });
    };

    render() {
        const {
            onChange,
            onKeyDown,
            userInput,
            state: { activeOption, filteredOptions, showOptions }
          } = this;
          let optionList;
        return (
            <div>
                <div style={search}>
                    <InputBase
                    placeholder="Searchâ€¦"
                    style={inputField}
                    onChange={onChange}
                    onKeyDown={onKeyDown}
                    value={userInput}
                    inputProps={{ 'aria-label': 'search' }}
                    />
                    <div style={searchIcon}>
                    <SearchIcon />
                    </div>
                    {optionList}
                </div>
            </div>
        )
    }
}

export default HeaderSearch
