import React, { Component } from 'react';
import { Map, GoogleApiWrapper, Marker, InfoWindow } from 'google-maps-react';
import Popup from './Popup'
import VerifiedLabel from './StatusLabel/VerifiedLabel';
import axios from 'axios'
import './MapContainer.scss'
import marker from '../../../Assets/Images/pin.png'

class MapContainer extends Component {

    constructor(props) {
        super(props)

        this.state = {
            stores: [],
            showingInfoWindow: false,
            activeMarker: {},
            selectedPlace: {}, 
        }
    }
    componentDidMount(){
        axios.get("https://praveesh91.github.io/test_json/mapData.json")
        .then(response =>{
            const data = response.data.stores;
            this.setState({
                stores:data
            })
        })

    }

    onMarkerClick = (props, marker, e) =>
        this.setState({
        selectedPlace: props,
        activeMarker: marker,
        showingInfoWindow: true
        });

    onMapClicked = (props) => {
        if (this.state.showingInfoWindow) {
        this.setState({
            showingInfoWindow: false,
            activeMarker: null
        })
        }
    };

    displayMarkers = () => {
        return this.state.stores.map((store, index) => {
          return <Marker 
            key={index} 
            id={index} 
            position={{
                lat: store.latitude,
                lng: store.longitude
            }}
            icon={{
                url: marker,
              }}
              onClick={this.onMarkerClick}
              name={'Current location'} />
        })
      }

render() {
    return (
        <div className='mapContainer'>
            <Map
                google={this.props.google}
                onClick={this.onMapClicked}
                zoom={9}
                initialCenter={{ lat: 47.444, lng: -122.176}}>
                {this.displayMarkers()}

                <InfoWindow
                    marker={this.state.activeMarker}
                    visible={this.state.showingInfoWindow}>
                    <div>
                        <h4>IBM Corporate Space</h4>
                        <VerifiedLabel />    
                    </div>
                </InfoWindow>

            </Map>

        </div>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: 'AIzaSyCq1D3cnZycxn5PCwiahpHscgep3yoN35U'
})(MapContainer);


