import React, {Component} from 'react';
import GoogleMapReact from "google-map-react";
import axios from 'axios'

import marker from '../../../Assets/Images/pin.png'

const googleAPIKey = "AIzaSyCq1D3cnZycxn5PCwiahpHscgep3yoN35U";

const markerStyle = {
  position: "absolute",
  top: "100%",
  left: "100%",
  cursor: "pointer",
  transform: "translate(-50%, -100%)"
};

class MapContainer extends Component {

    constructor(props) {
        super(props)

        this.state = {
            stores: [],
            center: {
                lat: 47.2, 
                lng: -122 
            },
            zoom: 9,
            showPopup: false
        }
    }

    componentDidMount(){
        axios.get("https://praveesh91.github.io/test_json/mapData.json")
        .then(response =>{
            const data = response.data.stores;
            this.setState({
                stores:data
            })
        })    
    }

    render() {


        const Marker = ({ center, zoom, data }) => {

            return (
            <GoogleMapReact
                bootstrapURLKeys={{ key: googleAPIKey }}
                yesIWantToUseGoogleMapApiInternals={true}
                defaultZoom={zoom}
                defaultCenter={center}
            >
                {data.map(item => {
                console.log("latitude", item.latitude)
                console.log("longitude",item.longitude)
                return (
                    <div 
                    lat={item.latitude} 
                    lng={item.longitude} 
                    onClick={() => {handleMarkerClick()}}>
                    <img style={markerStyle} src={marker} alt="pin" />
                    </div>
                );
                })}
            </GoogleMapReact>
            );
        };


        return (
            <div style={{ height: '100vh', width: '100%' }}>
                <Marker 
                    data={this.state.stores} 
                    center={this.state.center} 
                    zoom={this.state.zoom}
                    showPopup={this.state.showPopup} />
        </div>
        );
        }
    }

export default MapContainer;
