import React from 'react';
import { render } from 'react-dom';
import axios from 'axios';
import { Line, Bar, Pie } from 'react-chartjs-2';

class ChartContainer extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      options:{},
      chartData: {},
      showLine: true,
      showBar: false,
      showPie: false,
    }
  }

  componentDidMount() {
     axios('https://praveesh91.github.io/test_json/parkingData.json')
      .then((response) => {
        const { parkingData } = response.data;
        // const values = parkingData.map(d => d.vehicles.booked)
        // const valus = parkingData.map(d => d.day)
        const chartData = {
            labels: parkingData.map(k => k.day),
            datasets: [
                {
                label: 'Booked',
                // fill: 'false',
                data: parkingData.map(d => d.vehicles.booked),
                backgroundColor: 'red',
                borderColor: 'red',
                },
                // {
                //   label: 'Vacant',
                //   // fill: true,
                //   data: parkingData.map(d => d.vehicles.vacant),
                //   backgroundColor: 'blue',
                //   borderColor: 'blue',
                // },
                // {
                //   label: 'Occupied',
                //   // fill: 'false',
                //   data: parkingData.map(d => d.vehicles.occupied),
                //   backgroundColor: 'green',
                //   borderColor: 'green',
                // }
            ],
        }
        const options = [{
          fill : true,
        }] 

        this.setState({ chartData });
        this.setState({ options });
      });

  }

graphType = ()=>{

  const Chart_type = this.props.chartType;
  // console.log("Chart_type in ChartContainer is ",Chart_type);
  if (Chart_type==='Line'){
    this.setState({
      showLine: true,
      showBar: false,
      showPie: false,
    })
  }
  else if(Chart_type==='Bar'){
      this.setState({
        showLine: false,
        showBar: true,
        showPie: false,
      })
  }
  else if(Chart_type==="Pie"){
      this.setState({
        showLine: false,
        showBar: false,
        showPie: true,
      })
  }

}

render() {


  const {showLine, showBar, showPie} = this.state;
  console.log(this.state.options)

  // console.log(this.state)
  return (
  <div>
      {showLine && <Line data={this.state.chartData}/>}
      {showBar && <Bar data={this.state.chartData}/>}
      {showPie && <Line options={this.state.options} data={this.state.chartData}/>}
  </div>
  );
}
}
export default ChartContainer;
